/* Removed dark theme */

@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-family: var(--font-inter);
    cursor: default;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-heading);
    font-weight: 600;
    letter-spacing: -0.025em;
  }
}

@layer components {
  /* Premium Button Styles */
  .btn-primary {
    @apply bg-gradient-to-r from-blue-600 via-blue-700 to-blue-800 text-white font-semibold rounded-full shadow-xl hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-1 hover:scale-105 border-0 relative overflow-hidden;
  }



  .btn-secondary {
    @apply bg-white text-gray-800 font-semibold rounded-full border-2 border-gray-200 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 hover:bg-gray-50;
  }

  /* Advanced Gradient Text */
  .gradient-text {
    @apply bg-gradient-to-r from-blue-600 via-purple-600 to-blue-800 bg-clip-text text-transparent;
    background-size: 200% 200%;
    animation: gradient-shift 3s ease infinite;
  }

  @keyframes gradient-shift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  /* Premium Card Styles */
  .premium-card {
    @apply bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-500 border border-white/20 relative overflow-hidden;
  }

  .premium-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(59, 130, 246, 0.5), transparent);
  }

  .premium-card:hover {
    @apply transform -translate-y-2 scale-105;
  }

  /* Floating Animation */
  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-20px) rotate(5deg); }
  }

  /* Glowing Effect */
  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  @keyframes glow {
    from { box-shadow: 0 0 20px rgba(59, 130, 246, 0.5); }
    to { box-shadow: 0 0 30px rgba(59, 130, 246, 0.8), 0 0 40px rgba(59, 130, 246, 0.6); }
  }



  /* Advanced Fade In */
  .fade-in {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .fade-in.visible {
    opacity: 1;
    transform: translateY(0);
  }



  /* Skill Progress Bars */
  .skill-bar {
    @apply h-2 bg-gray-200 rounded-full overflow-hidden relative;
  }

  .skill-progress {
    @apply h-full bg-gradient-to-r from-blue-500 to-purple-600 rounded-full transition-all duration-1000 ease-out relative;
  }

  .skill-progress::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    background-image: linear-gradient(
      -45deg,
      rgba(255, 255, 255, .2) 25%,
      transparent 25%,
      transparent 50%,
      rgba(255, 255, 255, .2) 50%,
      rgba(255, 255, 255, .2) 75%,
      transparent 75%,
      transparent
    );
    background-size: 50px 50px;
    animation: move 2s linear infinite;
  }

  @keyframes move {
    0% { background-position: 0 0; }
    100% { background-position: 50px 50px; }
  }



  /* Navigation Glass Effect */
  .nav-glass {
    @apply bg-white/80 backdrop-blur-md border-b border-white/20;
  }

  .nav-link {
    @apply text-gray-700 hover:text-blue-600 font-medium transition-all duration-300 relative py-2;
  }

  .nav-link::after {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: 0;
    left: 50%;
    background: linear-gradient(90deg, #3b82f6, #8b5cf6);
    transition: all 0.3s ease;
  }

  .nav-link:hover::after {
    width: 100%;
    left: 0;
  }



  /* RTL Support */
  [dir="rtl"] {
    direction: rtl;
  }

  [dir="rtl"] .rtl-flip {
    transform: scaleX(-1);
  }

  [dir="rtl"] .text-left { 
    text-align: right; 
  }

  [dir="rtl"] .text-right { 
    text-align: left; 
  }

  [dir="rtl"] .ml-2 { 
    margin-right: 0.5rem; 
    margin-left: 0; 
  }

  [dir="rtl"] .mr-2 { 
    margin-left: 0.5rem; 
    margin-right: 0; 
  }

  [dir="rtl"] .flex-row {
    flex-direction: row-reverse;
  }

  /* Ensure proper spacing for Arabic text */
  [dir="rtl"] .space-x-4 > * + * {
    margin-right: 1rem;
    margin-left: 0;
  }

  [dir="rtl"] .space-x-8 > * + * {
    margin-right: 2rem;
    margin-left: 0;
  }

  /* Premium gradient text */
  .gradient-text {
    background: linear-gradient(135deg, hsl(199, 89%, 48%), hsl(198, 93%, 60%), hsl(201, 94%, 86%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-weight: 700;
  }

  /* Glass morphism effect */


  /* Premium card hover effects */
  .card-hover {
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    will-change: transform;
  }

  .card-hover:hover {
    transform: translateY(-12px) scale(1.02);
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.15);
  }

  /* Background patterns */
  .bg-dotted-pattern {
    background-image: radial-gradient(circle, rgba(255, 255, 255, 0.1) 1px, transparent 1px);
    background-size: 30px 30px;
  }



  /* Project card 3D tilt effect */
  .project-card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    transform-style: preserve-3d;
  }

  .project-card:hover {
    transform: translateY(-8px) rotateX(5deg) rotateY(-5deg) scale(1.02);
    box-shadow: 0 20px 40px rgba(56, 189, 248, 0.15);
  }

  /* Tech badge animations */
  .tech-badge {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    overflow: hidden;
  }

  .tech-badge::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(56, 189, 248, 0.2), transparent);
    transition: left 0.5s;
  }

  .tech-badge:hover {
    transform: scale(1.05) translateY(-2px);
    box-shadow: 0 8px 16px rgba(56, 189, 248, 0.2);
  }

  .tech-badge:hover::before {
    left: 100%;
  }

