  .fade-in {
    opacity: 0;
    transform: translateY(60px);
    transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .fade-in.visible {
    opacity: 1;
    transform: translateY(0);
  }

  .slide-in-left {
    opacity: 0;
    transform: translateX(-60px);
    transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .slide-in-left.visible {
    opacity: 1;
    transform: translateX(0);
  }

  .slide-in-right {
    opacity: 0;
    transform: translateX(60px);
    transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .slide-in-right.visible {
    opacity: 1;
    transform: translateX(0);
  }

  /* Premium navigation */
  .nav-glass {
    backdrop-filter: blur(20px);
    background: rgba(255, 255, 255, 0.8);
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
  }

  .nav-link {
    position: relative;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    font-weight: 500;
  }

  .nav-link:hover {
    color: hsl(199, 89%, 48%);
    transform: translateY(-1px);
  }

  .nav-link::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 50%;
    width: 0;
    height: 2px;
    background: hsl(199, 89%, 48%);
    transition: all 0.3s ease;
    transform: translateX(-50%);
  }

  .nav-link:hover::after {
    width: 100%;
  }

  /* Premium buttons with dark mode support */
  .btn-primary {
    background: linear-gradient(135deg, hsl(199, 89%, 48%), hsl(198, 93%, 60%));
    border: none;
    color: white;
    font-weight: 600;
    padding: 12px 32px;
    border-radius: 12px;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    overflow: hidden;
  }

  .btn-primary::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, hsl(198, 93%, 60%), hsl(199, 89%, 48%));
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 24px rgba(56, 189, 248, 0.3);
  }

  .btn-primary:hover::before {
    opacity: 1;
  }

  .btn-secondary {
    background: transparent;
    border: 2px solid hsl(199, 89%, 48%);
    color: hsl(199, 89%, 48%);
    font-weight: 600;
    padding: 10px 30px;
    border-radius: 12px;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .btn-secondary:hover {
    background: hsl(199, 89%, 48%);
    color: white;
    transform: translateY(-2px);
    box-shadow: 0 8px 16px rgba(56, 189, 248, 0.2);
  }

  /* Animated background elements */
  .floating-shapes {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    z-index: -1;
  }

  .floating-shape {
    position: absolute;
    background: linear-gradient(135deg, rgba(56, 189, 248, 0.1), rgba(56, 189, 248, 0.05));
    border-radius: 50%;
    animation: float 20s infinite linear;
  }

  .rtl-flip {
    transition: transform 0.2s ease;
  }

  [dir="rtl"] .rtl-flip {
    transform: scaleX(-1);
  }
}

@layer utilities {
  .animate-bounce-slow {
    animation: bounce 3s infinite;
  }

  .animate-fade-in {
    animation: fadeIn 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-slide-up {
    animation: slideUp 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse 4s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(60px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0) rotate(0deg);
  }
  25% {
    transform: translateY(-20px) rotate(90deg);
  }
  50% {
    transform: translateY(-10px) rotate(180deg);
  }
  75% {
    transform: translateY(-30px) rotate(270deg);
  }
}

@keyframes glow {
  from {
    box-shadow: 0 0 20px rgba(56, 189, 248, 0.3);
  }
  to {
    box-shadow: 0 0 40px rgba(56, 189, 248, 0.6);
  }
}

